import { postLlmChat, LlmHttpError } from '@/api/llm/client';
import { estimateQuantityPerPerson } from '@/core/calc';
import { churrascoProfile } from '@/profiles/churrasco';
import type { ConversationState, EventStatus } from '@/types/domain';

type ChatInput = { text: string; userId: string; eventoId?: string };
type ChatContext = { idempotencyKey?: string };

type ToolCall = { name: string; arguments: Record<string, unknown> | unknown[] };

type ChatUiPayload = {
  estado: EventStatus | string;
  evento_id: string | null;
  mensagem: string;
  toolCalls: ToolCall[];
  ctas: string[];
};

const suggestNextActions = (state?: ConversationState): string[] => {
  // Sugestões simples conforme estado atual
  const rawCtx: Record<string, unknown> | undefined = state?.context as Record<string, unknown> | undefined;
  const rawEstado = rawCtx ? rawCtx['estado'] : undefined;
  const st = typeof rawEstado === 'string' ? rawEstado : 'collecting_core';
  if (st === 'collecting_core') return ['adicionar_itens', 'definir_qtd_pessoas', 'confirmar_data_local'];
  if (st === 'itens_pendentes_confirmacao') return ['confirmar_itens', 'distribuir_responsabilidades'];
  if (st === 'distrib_pendente_confirmacao') return ['confirmar_distribuicao', 'ver_resumo_custos'];
  return ['ajuda', 'ver_evento'];
};

const isStructuredCta = (text: string): { action: string; args?: Record<string, any> } | null => {
  const t = text.trim().toLowerCase();
  // Exemplos básicos de CTA determinísticos
  if (/^(criar|novo)\s+evento/.test(t)) return { action: 'create_event' };
  if (/^abrir\s+evento\s+([a-f0-9\-]{6,})$/.test(t)) {
    const m = t.match(/^abrir\s+evento\s+([a-f0-9\-]{6,})$/);
    return { action: 'open_event', args: { evento_id: m?.[1] } };
  }
  if (/^confirmar\s+(itens|distribuicao)$/.test(t)) {
    const kind = t.includes('distribuicao') ? 'distribution' : 'items';
    return { action: `confirm_${kind}` };
  }
  return null;
};

const isGreetingOrStatus = (text: string): boolean => {
  const t = text.toLowerCase();
  return /(ol[aá]|oi|bom\s+dia|boa\s+tarde|boa\s+noite|tudo\s+bem|como\s+est[aá])/.test(t) || /(status|como\s+est[aá]\s+o\s+evento)/.test(t);
};

const tryParseEditableCommand = (text: string): ToolCall | null => {
  const t = text.toLowerCase();
  // Padrões: "muda picanha para 1.5kg" | "altera bebidas para 20 L"
  const m = t.match(/\b(muda|altera)\s+(.+?)\s+para\s+([0-9.,]+)\s*(kg|g|l|unid)?\b/);
  if (m) {
    const item = m[2].trim();
    const quantidadeRaw = m[3].replace(',', '.');
    const quantidade = Number(quantidadeRaw);
    const unidade = m[4] || 'unid';
    if (!Number.isNaN(quantidade)) {
      return { name: 'update_item_quantity', arguments: { item, quantidade, unidade } };
    }
  }
  return null;
};

const generateIdempotencyKey = (text: string): string => {
  // Hash simples: conteúdo + timestamp curto (segundos)
  const ts = Math.floor(Date.now() / 1000);
  const base = `${text}:${ts}`;
  let hash = 0;
  for (let i = 0; i < base.length; i++) {
    hash = (hash << 5) - hash + base.charCodeAt(i);
    hash |= 0; // 32-bit
  }
  return `chat-${Math.abs(hash)}`;
};

const fallbackHeuristic = (text: string, state?: ConversationState): ChatUiPayload => {
  const t = text.toLowerCase();
  const rawCtx: Record<string, unknown> | undefined = state?.context as Record<string, unknown> | undefined;
  const prevTipo = typeof rawCtx?.['tipo_evento'] === 'string' ? (rawCtx?.['tipo_evento'] as string) : undefined;
  const prevPessoas = typeof rawCtx?.['qtd_pessoas'] === 'number' ? (rawCtx?.['qtd_pessoas'] as number) : undefined;
  const tipo = /(churrasco|pizza|feijoada|anivers[aá]rio|jantar)/.exec(t)?.[1] || prevTipo || 'evento';
  // Detectar pessoas em várias formas: "para 10", "10 pessoas", "são 10 pessoas", "10p"
  const m1 = /\b(?:p\/|para|pra)\s*(\d{1,3})\b/.exec(t);
  const m2 = /\b(\d{1,3})\s*(pessoas|p|ppl)\b/.exec(t);
  const m3 = /\b(s[aã]o|tem)\s*(\d{1,3})\s*pessoas\b/.exec(t);
  const pessoas = m1 ? Number(m1[1]) : m2 ? Number(m2[1]) : m3 ? Number(m3[2]) : prevPessoas;

  // Confirmações sem novos dados: ok, beleza, sim
  const confirma = /(ok|beleza|sim|perfeito|isso)/.test(t);
  const hasCore = Boolean(tipo && pessoas);
  let mensagem = hasCore
    ? `Entendi: ${tipo} para ${pessoas} pessoas. Posso sugerir itens e quantidades.`
    : `Entendi: ${tipo}. Me diga quantas pessoas para sugerir itens.`;
  if (confirma && hasCore) {
    mensagem = `Ótimo! Vou sugerir itens e quantidades para ${tipo} de ${pessoas} pessoas.`;
  }

  const toolCalls: ToolCall[] = [];
  if (tipo === 'churrasco') {
    // Sugestões baseadas no perfil + quantidade por pessoa
    const carnePer = estimateQuantityPerPerson('carne');
    const bebidaPer = estimateQuantityPerPerson('bebida');
    const carneTotal = pessoas ? carnePer * pessoas : churrascoProfile.defaults?.suggestedItems?.find((i) => i.id === 'carne')?.quantity || 500;
    const bebidaTotal = pessoas ? bebidaPer * pessoas : churrascoProfile.defaults?.suggestedItems?.find((i) => i.id === 'bebidas')?.quantity || 20;
    toolCalls.push(
      { name: 'suggest_items', arguments: [
        { nome_item: 'Carne', quantidade: carneTotal, unidade: 'g', prioridade: 'A' },
        { nome_item: 'Bebidas', quantidade: bebidaTotal, unidade: 'L', prioridade: 'A' },
      ] },
    );
  }

  const rawEstado = rawCtx ? rawCtx['estado'] : undefined;
  const estado = typeof rawEstado === 'string' ? rawEstado : 'collecting_core';
  const eventoIdFromCtx = typeof rawCtx?.['eventoId'] === 'string' ? (rawCtx?.['eventoId'] as string) : null;
  return {
    estado,
    evento_id: eventoIdFromCtx,
    mensagem,
    toolCalls,
    ctas: suggestNextActions(state),
  };
};

export async function orchestrate(input: ChatInput, state?: ConversationState, ctx?: ChatContext): Promise<ChatUiPayload> {
  // a) CTA/ação estruturada
  const cta = isStructuredCta(input.text);
  if (cta) {
    const mensagem = cta.action === 'create_event'
      ? 'Ok! Vou iniciar um rascunho de evento. Me diga o tipo e a quantidade de pessoas.'
      : cta.action === 'open_event'
        ? `Abrindo evento ${cta.args?.evento_id ?? input.eventoId ?? ''}.`
        : 'Certo! Vou prosseguir com a confirmação.';
    return {
      estado: (state as any)?.estado ?? 'collecting_core',
      evento_id: cta.args?.evento_id ?? input.eventoId ?? null,
      mensagem,
      toolCalls: cta.action !== 'create_event' ? [{ name: cta.action, arguments: cta.args ?? {} }] : [],
      ctas: suggestNextActions(state),
    };
  }

  // b) Greeting/status simples
  if (isGreetingOrStatus(input.text)) {
    const mensagem = 'Olá! Posso ajudar a planejar seu evento. Me diga o tipo e o número de pessoas.';
    return {
      estado: (state as any)?.estado ?? 'collecting_core',
      evento_id: input.eventoId ?? null,
      mensagem,
      toolCalls: [],
      ctas: suggestNextActions(state),
    };
  }

  // c) Comando editável
  const editCall = tryParseEditableCommand(input.text);
  if (editCall) {
    const { item, quantidade, unidade } = editCall.arguments || {};
    const mensagem = `Ok, vou alterar "${item}" para ${quantidade} ${unidade}.`;
    return {
      estado: (state as any)?.estado ?? 'collecting_core',
      evento_id: input.eventoId ?? null,
      mensagem,
      toolCalls: [editCall],
      ctas: suggestNextActions(state),
    };
  }

  // d) Caso contrário -> chamar postLlmChat com histórico quando disponível
  const idem = ctx?.idempotencyKey ?? generateIdempotencyKey(input.text);
  try {
    const prior = (state?.history || []).map((m) => ({ role: m.role as 'user' | 'assistant' | 'system', content: m.content }));
    const res = await postLlmChat({
      messages: [...prior, { role: 'user', content: input.text }],
      temperature: 0.2,
      idempotencyKey: idem,
    });
    const rawCtx: Record<string, unknown> | undefined = state?.context as Record<string, unknown> | undefined;
    const rawEstado = rawCtx ? rawCtx['estado'] : undefined;
    const estado = typeof rawEstado === 'string' ? rawEstado : 'collecting_core';
    const eventoIdFromCtx = typeof rawCtx?.['eventoId'] === 'string' ? (rawCtx?.['eventoId'] as string) : null;
    return {
      estado,
      evento_id: eventoIdFromCtx ?? input.eventoId ?? null,
      mensagem: res.content,
      toolCalls: res.toolCalls ?? [],
      ctas: suggestNextActions(state),
    };
  } catch (e) {
    // e) Fallback: se /llm-chat falhar, usar heurística
    if (e instanceof LlmHttpError) {
      console.debug('[ChatOrchestrator] LLM falhou', { code: e.code, status: e.status, msg: e.message });
    } else {
      console.debug('[ChatOrchestrator] LLM falhou (erro desconhecido)');
    }
    return fallbackHeuristic(input.text, state);
  }
}

import { useState, useEffect } from 'react';
import { supabase } from '@/integrations/supabase/client';
import { useAuth } from './useAuth';
// Force TypeScript refresh

interface Event {
  id: number;
  title: string;
  description: string | null;
  event_date: string;
  event_time: string;
  location: string | null;
  user_id: string;
  max_attendees: number | null;
  is_public: boolean | null;
  status: string | null;
}

interface EventOrganizer {
  id: string;
  user_id: string;
  added_by: string;
  added_at: string;
}

export const useEvent = (eventId: string) => {
  const eventIdNumber = parseInt(eventId);
  const { user } = useAuth();
  const [event, setEvent] = useState<Event | null>(null);
  const [organizers, setOrganizers] = useState<EventOrganizer[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  // Verifica se o usuário atual é organizador do evento
  const isOrganizer = () => {
    if (!user || !event) return false;
    
    // É criador do evento
    if (event.user_id === user.id) return true;
    
    // É co-organizador
    return organizers.some(org => org.user_id === user.id);
  };

  // Busca dados do evento
  const fetchEvent = async () => {
    try {
      setLoading(true);
      const { data, error } = await supabase
        .from('table_reune')
        .select('*')
        .eq('id', eventIdNumber)
        .single();

      if (error) throw error;
      setEvent(data);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Erro ao carregar evento');
    } finally {
      setLoading(false);
    }
  };

  // Busca co-organizadores do evento
  const fetchOrganizers = async () => {
    try {
      const { data, error } = await supabase
        .from('event_organizers')
        .select('*')
        .eq('event_id', eventIdNumber);

      if (error) throw error;
      setOrganizers(data || []);
    } catch (err) {
      console.error('Erro ao buscar organizadores:', err);
    }
  };

  // Adiciona co-organizador
  const addOrganizer = async (userId: string) => {
    if (!user) return { error: 'Usuário não autenticado' };

    try {
      const { error } = await supabase
        .from('event_organizers')
        .insert({
          event_id: eventIdNumber,
          user_id: userId,
          added_by: user.id
        });

      if (error) throw error;
      
      await fetchOrganizers(); // Atualiza a lista
      return { error: null };
    } catch (err) {
      return { error: err instanceof Error ? err.message : 'Erro ao adicionar organizador' };
    }
  };

  // Remove co-organizador
  const removeOrganizer = async (organizerId: string) => {
    try {
      const { error } = await supabase
        .from('event_organizers')
        .delete()
        .eq('id', organizerId);

      if (error) throw error;
      
      await fetchOrganizers(); // Atualiza a lista
      return { error: null };
    } catch (err) {
      return { error: err instanceof Error ? err.message : 'Erro ao remover organizador' };
    }
  };

  useEffect(() => {
    if (eventId && !isNaN(eventIdNumber)) {
      fetchEvent();
      fetchOrganizers();
    }
  }, [eventId, eventIdNumber]);

  return {
    event,
    organizers,
    loading,
    error,
    isOrganizer: isOrganizer(),
    addOrganizer,
    removeOrganizer,
    refetch: () => {
      fetchEvent();
      fetchOrganizers();
    }
  };
};

import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { ArrowLeft } from 'lucide-react';
import { supabase } from '@/integrations/supabase/client';
import { useAuth } from '@/hooks/useAuth';
import { useToast } from '@/hooks/use-toast';
// TS type refresh

interface CreateEventProps {
  onBack: () => void;
  onCreate: () => void;
}

const CreateEvent = ({ onBack, onCreate }: CreateEventProps) => {
  const { user } = useAuth();
  const { toast } = useToast();
  const [title, setTitle] = useState('');
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  const [location, setLocation] = useState('');
  const [description, setDescription] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    
    if (!user) {
      toast({
        title: "Erro",
        description: "Você precisa estar logado para criar um evento.",
        variant: "destructive",
      });
      return;
    }

    setLoading(true);
    
    try {
      const { error } = await supabase
        .from('table_reune')
        .insert({
          title,
          event_date: date,
          event_time: time,
          location,
          description,
          user_id: user.id,
          is_public: true,
          status: 'published'
        });

      if (error) throw error;

      toast({
        title: "Evento criado com sucesso!",
        description: "Seu evento foi publicado e já está disponível.",
      });

      onCreate();
    } catch (error: any) {
      toast({
        title: "Erro ao criar evento",
        description: error.message || "Ocorreu um erro. Tente novamente.",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-background">
      <header className="bg-card border-b px-4 py-6">
        <div className="max-w-2xl mx-auto flex items-center">
          <Button variant="ghost" size="sm" onClick={onBack} className="mr-4">
            <ArrowLeft className="w-4 h-4" />
          </Button>
          <div>
            <h1 className="text-xl font-semibold">Criar Novo Evento</h1>
            <p className="text-muted-foreground text-sm">Preencha as informações do seu evento</p>
          </div>
        </div>
      </header>

      <main className="max-w-2xl mx-auto p-4">
        <Card>
          <CardHeader>
            <CardTitle>Informações do Evento</CardTitle>
            <CardDescription>
              Adicione os detalhes principais do seu evento
            </CardDescription>
          </CardHeader>
          <CardContent>
            <form onSubmit={handleSubmit} className="space-y-6">
              <div>
                <Label htmlFor="title">Nome do Evento</Label>
                <Input
                  id="title"
                  type="text"
                  placeholder="Ex: Churrasco de Domingo"
                  value={title}
                  onChange={(e) => setTitle(e.target.value)}
                  required
                />
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="date">Data</Label>
                  <Input
                    id="date"
                    type="date"
                    value={date}
                    onChange={(e) => setDate(e.target.value)}
                    required
                  />
                </div>
                <div>
                  <Label htmlFor="time">Horário</Label>
                  <Input
                    id="time"
                    type="time"
                    value={time}
                    onChange={(e) => setTime(e.target.value)}
                    required
                  />
                </div>
              </div>

              <div>
                <Label htmlFor="location">Endereço</Label>
                <Input
                  id="location"
                  type="text"
                  placeholder="Ex: Rua das Flores, 123 - Centro"
                  value={location}
                  onChange={(e) => setLocation(e.target.value)}
                  required
                />
              </div>

              <div>
                <Label htmlFor="description">Descrição (opcional)</Label>
                <Textarea
                  id="description"
                  placeholder="Adicione detalhes sobre o evento..."
                  value={description}
                  onChange={(e) => setDescription(e.target.value)}
                  rows={4}
                />
              </div>

              <Button 
                type="submit" 
                className="w-full"
                disabled={loading || !title || !date || !time || !location}
              >
                {loading ? 'Criando...' : 'Criar Evento'}
              </Button>
            </form>
          </CardContent>
        </Card>
      </main>
    </div>
  );
};

export default CreateEvent;

import { useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Plus, Calendar, Clock, MapPin, Users, LogOut, AlertTriangle } from 'lucide-react';
import { cn } from '@/lib/utils';
import { supabase } from '@/integrations/supabase/client';
import { useToast } from '@/hooks/use-toast';
import { useAuth } from '@/hooks/useAuth';
import reUneLogo from '@/assets/reune-logo.png';
// Refresh TS types

interface Event {
  id: number;
  title: string;
  event_date: string;
  event_time: string;
  location: string | null;
  description: string | null;
  user_id: string;
  is_public: boolean | null;
  status: string | null;
  created_at: string;
  isOwner: boolean;
  isConfirmed: boolean;
}

interface DashboardProps {
  userEmail: string;
  onCreateEvent: () => void;
  onViewEvent: (eventId: string) => void;
  onLogout: () => void;
}

const Dashboard = ({ userEmail, onCreateEvent, onViewEvent, onLogout }: DashboardProps) => {
  const { user, devModeActive, disableDevMode } = useAuth();
  const { toast } = useToast();
  const isDevMode = import.meta.env.VITE_DEV_MODE === 'true';
  const [events, setEvents] = useState<Event[]>([]);
  const [loading, setLoading] = useState(true);

  const handleLogout = async () => {
    try {
      if (devModeActive) {
        // Modo dev: apenas desativa o modo dev
        disableDevMode();
        toast({
          title: "Modo DEV desativado!",
          description: "Voltando ao login",
        });
      } else {
        // Modo normal: faz logout real do Supabase
        const { error } = await supabase.auth.signOut();
        if (error) throw error;
        
        toast({
          title: "Logout realizado com sucesso!",
          description: "Até logo!",
        });
      }
      
      onLogout();
    } catch (error) {
      const err = error as { message?: string };
      toast({
        title: "Erro no logout",
        description: err?.message || "Ocorreu um erro. Tente novamente.",
        variant: "destructive",
      });
    }
  };

  const fetchEvents = async () => {
    if (!user) return;

    try {
      setLoading(true);
      
      // Busca eventos e confirmações
      const { data: eventsData, error: eventsError } = await supabase
        .from('table_reune')
        .select('*')
        .or(`user_id.eq.${user.id},is_public.eq.true`)
        .order('event_date', { ascending: true });

      if (eventsError) throw eventsError;

      // Busca confirmações do usuário
      const { data: confirmationsData, error: confirmationsError } = await supabase
        .from('event_confirmations')
        .select('event_id, presence_confirmed')
        .eq('user_id', user.id);

      if (confirmationsError) throw confirmationsError;

      // Cria um mapa de confirmações
      const confirmationsMap = new Map(
        confirmationsData?.map(c => [c.event_id, c.presence_confirmed]) || []
      );

      const eventsWithStatus = eventsData.map(event => ({
        ...event,
        isOwner: event.user_id === user.id,
        isConfirmed: confirmationsMap.get(event.id) === true
      }));

      setEvents(eventsWithStatus);
    } catch (error) {
      const err = error as { message?: string };
      toast({
        title: "Erro ao carregar eventos",
        description: err?.message || "Não foi possível carregar os eventos.",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchEvents();
  }, [user?.id]);

  const formatDate = (dateStr: string) => {
    const date = new Date(dateStr);
    return date.toLocaleDateString('pt-BR', {
      day: 'numeric',
      month: 'short'
    });
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary mx-auto"></div>
          <p className="mt-4 text-muted-foreground">Carregando eventos...</p>
        </div>
      </div>
    );
  }

  const cardColors = [
    'bg-orange-light border-orange/20',
    'bg-gold-light border-gold/20',
    'bg-teal-light border-teal/20',
    'bg-cyan-light border-cyan/20',
    'bg-orange-light border-orange/30',
    'bg-cyan-light border-cyan/30'
  ];

  const getCardColor = (index: number) => {
    return cardColors[index % cardColors.length];
  };

  return (
    <div className="min-h-screen bg-background">
      {(isDevMode && devModeActive) && (
        <div className="fixed top-4 right-4 bg-secondary/90 text-secondary-foreground px-3 py-2 rounded-lg flex items-center gap-2 text-sm font-medium z-50">
          <AlertTriangle className="w-4 h-4" />
          LOGADO COMO DEV
        </div>
      )}
      <header className="bg-card/50 backdrop-blur-sm border-b border-border/50 px-6 py-8">
        <div className="max-w-6xl mx-auto flex justify-between items-center">
          <div className="flex items-center gap-4 animate-fade-in">
            <img src={reUneLogo} alt="ReUNE Logo" className="h-12 w-auto" />
            <div>
              <h1 className="text-3xl font-bold text-primary tracking-tight">ReUNE</h1>
              <p className="text-muted-foreground font-medium">Olá, {userEmail}!</p>
            </div>
          </div>
          <Button 
            variant="ghost" 
            size="sm" 
            onClick={handleLogout}
            className="text-muted-foreground hover:text-foreground"
          >
            <LogOut className="w-4 h-4 mr-2" />
            Sair
          </Button>
        </div>
      </header>

      <main className="max-w-6xl mx-auto p-6 animate-fade-in">
        <div className="mb-12">
          <h2 className="text-2xl font-semibold mb-6 text-foreground">Meus Eventos</h2>
          <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
            {events.filter(event => event.isOwner || event.isConfirmed).length === 0 ? (
              <div className="col-span-full text-center py-12">
                <p className="text-muted-foreground mb-4">Você ainda não tem eventos confirmados.</p>
                <Button onClick={onCreateEvent} variant="outline">
                  <Plus className="w-4 h-4 mr-2" />
                  Criar Primeiro Evento
                </Button>
              </div>
            ) : (
              events.filter(event => event.isOwner || event.isConfirmed).map((event, index) => (
                <Card 
                  key={event.id} 
                  className={cn(
                    "cursor-pointer group animate-scale-in",
                    getCardColor(index)
                  )}
                  onClick={() => onViewEvent(event.id.toString())}
                  style={{ animationDelay: `${index * 100}ms` }}
                >
                  <CardHeader className="pb-4">
                    <div className="flex justify-between items-start">
                      <div>
                        <p className="text-xs font-medium text-muted-foreground mb-1 uppercase tracking-wide">Eventos</p>
                        <CardTitle className="text-xl font-bold text-foreground group-hover:text-primary transition-colors">
                          {event.title}
                        </CardTitle>
                      </div>
                      <Badge variant="secondary" className="bg-white/80 text-foreground text-xs px-3 py-1">
                        {event.isOwner ? 'Organizador' : 'Confirmado'}
                      </Badge>
                    </div>
                  </CardHeader>
                  <CardContent>
                    <div className="space-y-3">
                      <div className="flex items-center text-sm font-medium text-foreground/80">
                        <Calendar className="w-4 h-4 mr-3 text-primary" />
                        {formatDate(event.event_date)} • {event.event_time}
                      </div>
                      <div className="flex items-center text-sm text-foreground/70">
                        <MapPin className="w-4 h-4 mr-3 text-primary" />
                        {event.location || 'Local não informado'}
                      </div>
                    </div>
                  </CardContent>
                </Card>
              ))
            )}
          </div>
        </div>

        <div className="mb-12">
          <h2 className="text-2xl font-semibold mb-6 text-foreground">Eventos Pendentes</h2>
          <p className="text-sm text-muted-foreground mb-4">Eventos aguardando sua confirmação</p>
          <div className="flex flex-wrap gap-3">
            {events.filter(event => !event.isOwner && !event.isConfirmed).length === 0 ? (
              <p className="text-muted-foreground text-sm">Nenhum evento pendente.</p>
            ) : (
              events.filter(event => !event.isOwner && !event.isConfirmed).map((event, index) => (
                <div
                  key={event.id}
                  onClick={() => onViewEvent(event.id.toString())}
                  className={cn(
                    "px-6 py-4 rounded-xl cursor-pointer transition-all hover:scale-105 hover:shadow-lg",
                    "border-2 flex items-center gap-3 animate-scale-in",
                    getCardColor(index + 3)
                  )}
                  style={{ animationDelay: `${(index + 3) * 100}ms` }}
                >
                  <div className="flex items-center gap-2">
                    <Calendar className="w-5 h-5 text-primary" />
                    <div>
                      <p className="font-semibold text-foreground">{event.title}</p>
                      <p className="text-xs text-muted-foreground">
                        {formatDate(event.event_date)} • {event.event_time}
                      </p>
                    </div>
                  </div>
                  <Badge variant="secondary" className="ml-2 text-xs">
                    Pendente
                  </Badge>
                </div>
              ))
            )}
          </div>
        </div>
      </main>

      {/* Floating Action Button */}
      <Button
        onClick={onCreateEvent}
        variant="floating"
        size="lg"
        className="fixed bottom-8 right-8 rounded-full w-16 h-16 text-lg animate-scale-in"
        style={{ animationDelay: '600ms' }}
      >
        <Plus className="w-7 h-7" />
      </Button>
    </div>
  );
};

export default Dashboard;

import { useState, useEffect } from 'react';
import { useAuth } from '@/hooks/useAuth';
import { supabase } from '@/integrations/supabase/client';
import Login from './Login';
import Dashboard from './Dashboard';
import CreateEvent from './CreateEvent';
import EventDetails from './EventDetails';
import ChatWidget from '@/components/ChatWidget';

type Screen = 'login' | 'dashboard' | 'createEvent' | 'eventDetails';

const Index = () => {
  const { user, loading, enableDevMode } = useAuth();
  const [currentScreen, setCurrentScreen] = useState<Screen>('login');
  const [selectedEventId, setSelectedEventId] = useState<string>('');
  
  const isDevMode = import.meta.env.VITE_DEV_MODE === 'true';

  useEffect(() => {
    // Verifica se o modo dev está ativo no localStorage
    const devModeFromStorage = isDevMode && localStorage.getItem('devModeActive') === 'true';
    
    if (devModeFromStorage) {
      setCurrentScreen('dashboard');
      return;
    }
    
    if (user) {
      setCurrentScreen('dashboard');
    } else if (!loading) {
      setCurrentScreen('login');
    }
  }, [user, loading]);

  const handleLogin = () => {
    setCurrentScreen('dashboard');
  };

  const handleLogout = async () => {
    await supabase.auth.signOut();
    setCurrentScreen('login');
  };

  const handleCreateEvent = () => {
    setCurrentScreen('createEvent');
  };

  const handleViewEvent = (eventId: string) => {
    setSelectedEventId(eventId);
    setCurrentScreen('eventDetails');
  };

  const handleBackToDashboard = () => {
    setCurrentScreen('dashboard');
  };

  const handleEventCreated = () => {
    setCurrentScreen('dashboard');
  };

  const renderScreen = () => {
    if (loading) {
      return (
        <div className="min-h-screen bg-background flex items-center justify-center">
          <div className="text-center">
            <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary mx-auto"></div>
            <p className="mt-4 text-muted-foreground">Carregando...</p>
          </div>
        </div>
      );
    }

    if (!user && currentScreen !== 'login') {
      // Verifica se o modo dev está ativo antes de forçar login
      const devModeActive = isDevMode && localStorage.getItem('devModeActive') === 'true';
      if (!devModeActive) {
        return <Login onLogin={handleLogin} />;
      }
    }

    switch (currentScreen) {
      case 'login':
        return <Login onLogin={handleLogin} />;
      
      case 'dashboard':
        return (
          <>
            <Dashboard
              userEmail={user?.email || ''}
              onCreateEvent={handleCreateEvent}
              onViewEvent={handleViewEvent}
              onLogout={handleLogout}
            />
            <ChatWidget />
          </>
        );
      
      case 'createEvent':
        return (
          <>
            <CreateEvent
              onBack={handleBackToDashboard}
              onCreate={handleEventCreated}
            />
            <ChatWidget />
          </>
        );
      
      case 'eventDetails':
        return (
          <>
            <EventDetails
              eventId={selectedEventId}
              onBack={handleBackToDashboard}
            />
            <ChatWidget />
          </>
        );
      
      default:
        return <Login onLogin={handleLogin} />;
    }
  };

  return renderScreen();
};

export default Index;
